library ieee;
use ieee.std_logic_1164.all;

--Look Ahead Carry four-bit Adder, Time loss: 9ns--
entity fouradder is
    port(
        a, b: in std_logic_vector(3 downto 0);
        cin: in std_logic;
        s: out std_logic_vector(3 downto 0);
        cout: out std_logic
    );
end fouradder;


library ieee;
use ieee.std_logic_1164.all;
--一位全加器--
entity fulladder is
    port(
        a, b, ci: in std_logic;
        s: out std_logic;
        p, g: buffer std_logic
    );
end fulladder;


library ieee;
use ieee.std_logic_1164.all;
--一位半加器--
entity halfadder is
    port(
        ain, bin: in std_logic;
        so, co: out std_logic
    );
end halfadder;

architecture bhv_half of halfadder is
begin
    co <= ain and bin;--for G_n
    so <= ain xor bin;--for P_n
end bhv_half;

architecture bhv_full of fulladder is
    component halfadder
        port(
            ain, bin: in std_logic;
            so, co: out std_logic
        );
    end component;
begin
    u1: halfadder port map(ain=>a, bin=>b, so=>p, co=>g);
    s <= ci xor p;
end bhv_full;

architecture bhv of fouradder is
    component fulladder
        port(
            a, b, ci: in std_logic;
            p, g, s: out std_logic
        );
    end component;
    signal p, g, c: std_logic_vector(3 downto 0);
begin
    u0: fulladder port map(a=>a(0), b=>b(0), ci=>cin, p=>p(0), g=>g(0), s=>s(0));
    u1: for i in 1 to 3 generate--使用生成语句，元件例化
        ux: fulladder port map(a=>a(i), b=>b(i), ci=>c(i-1), p=>p(i), g=>g(i), s=>s(i));
    end generate;
    --超前进位部分--
    c(0) <= g(0) or (p(0) and cin);
    c(1) <= g(1) or (p(1) and g(0)) or (p(1) and p(0) and cin);
    c(2) <= g(2) or (p(2) and g(1)) or (p(2) and p(1) and g(0)) 
                 or (p(2) and p(1) and p(0) and cin);
    c(3) <= g(3) or (p(3) and g(2)) or (p(3) and p(2) and g(1)) 
                 or (p(3) and p(2) and p(1) and g(0)) 
                 or (p(3) and p(2) and p(1) and p(0) and cin);
    cout <= c(3);
end bhv;
